// Elevation presets based on common use cases
export const elevationPresets = {
  card: elevation.md,
  modal: elevation.xl,
  dropdown: elevation.lg,
  tooltip: elevation.sm,
  navigation: elevation.md,
  floatingButton: elevation.lg,
};

// Function to apply elevation preset
export const applyElevation = (presetName: keyof typeof elevationPresets) => {
  return { boxShadow: elevationPresets[presetName] };
};