import React from 'react';
import { colors, spacing, typography } from '@repo/design-system';
import { motion } from '@repo/design-system/motion';

interface InputProps extends React.InputHTMLAttributes<HTMLInputElement> {
  label?: string;
  error?: string;
  helperText?: string;
  startAdornment?: React.ReactNode;
  endAdornment?: React.ReactNode;
}

export const Input: React.FC<InputProps> = ({
  label,
  error,
  helperText,
  startAdornment,
  endAdornment,
  className = '',
  ...props
}) => {
  const hasError = !!error;
  
  return (
    <div className={`w-full ${className}`}>
      {label && (
        <label 
          className={`block text-sm font-medium mb-1 ${
            hasError ? 'text-red-600' : 'text-gray-700'
          }`}
        >
          {label}
        </label>
      )}
      
      <div className="relative">
        {startAdornment && (
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            {startAdornment}
          </div>
        )}
        
        <input
          className={`
            w-full rounded-md border ${
              hasError 
                ? 'border-red-500 focus:ring-red-500 focus:border-red-500' 
                : 'border-gray-300 focus:ring-blue-500 focus:border-blue-500'
            } 
            py-2 pl-4 pr-10 text-base shadow-sm
            focus:outline-none focus:ring-2 focus:ring-offset-2
            transition ${motion.duration.standard} ${motion.easing.standard}
          `}
          style={{ transition: `all ${motion.duration.standard} ${motion.easing.standard}` }}
          {...props}
        />
        
        {endAdornment && (
          <div className="absolute inset-y-0 right-0 pr-3 flex items-center">
            {endAdornment}
          </div>
        )}
      </div>
      
      {helperText && !hasError && (
        <p className="mt-1 text-sm text-gray-500">{helperText}</p>
      )}
      
      {error && (
        <p className="mt-1 text-sm text-red-600">{error}</p>
      )}
    </div>
  );
};